mapper.select:
select ADMIN_ID "adminId",ADMIN_ACCOUNT "account",ADMIN_PWD "password",ADMIN_NAME "name",ROLE_ID "roleId"
,ADMIN_STATE "state",to_char(ADMIN_CDATE,'YYYY-MM-DD') "createTime" from T_ADMIN

	
		and ADMIN_ID=#{adminId}
	
	
		and ADMIN_ACCOUNT=#{account}
	
	
		and ADMIN_PWD=#{password}
	
	
		and ADMIN_STATE=#{state}

select a.ADMIN_ID,a.ADMIN_ACCOUNT,a.admin_pwd, a.admin_name, to_char(a.admin_cdate,'yyyy-mm-dd hh24:mi:ss') admin_cdate,a.role_id,t.role_name ,p.parm_type,a.admin_state,p.PARM_NAME from t_admin a,t_parm p, t_role t where a.admin_state=p.parm_val and p.parm_pid=2 and a.role_id=t.role_id
and a.admin_name LIKE '%${name}%'
and a.admin_account LIKE '%${account}%'
and a.ADMIN_STATE=#{state}
and a.ROLE_ID=#{roleId}
order by a.ADMIN_ID asc
